mapper.select:
SELECT
node_id,
node_name
FROM
node
WHERE
node_id IN (

    #{nodeId}

);

select from node
1=1
and status = #{status1}
and is_settle = #{isSettle1}
union select from node
1=1
and status = #{status2}
and is_settle = #{isSettle2}
ORDER BY big_version DESC, total_value DESC, staking_block_num ASC, staking_tx_index ASC
