mapper.select:
SELECT t1.token_address AS contract, t1.token_id AS tokenId, t1.address, t1.balance, t2.image, t2.token_tx_qty AS txCount FROM token_1155_holder t1 LEFT JOIN token_1155_inventory t2 ON t1.token_address = t2.token_address AND t1.token_id = t2.token_id WHERE 1 = 1
AND t1.token_address = #{key.tokenAddress}
AND t1.address = #{key.address}
AND t1.token_id = #{key.tokenId}
AND t1.balance != 0

SELECT th.token_address AS tokenAddress, th.address, t.type, ti.NAME, ti.DECIMAL, '' AS symbol, t.total_supply AS totalSupply, th.balance, th.token_owner_tx_qty AS txCount, ti.token_id as tokenId, t.create_time as createTime FROM token_1155_holder th LEFT JOIN token t ON th.token_address = t.address LEFT JOIN token_1155_inventory ti ON th.token_address = ti.token_address AND th.token_id = ti.token_id WHERE th.balance != 0
and th.token_address = #{tokenAddress}
and th.address = #{address}
AND t.type = "erc1155" ORDER BY balance DESC

SELECT th.token_address AS tokenAddress, th.address, ti.token_id AS tokenId, 'erc1155' AS type, '' AS symbol, ti.`name`, ti.`decimal`, 0 AS totalSupply, th.token_owner_tx_qty AS txCount, th.balance FROM token_1155_holder th LEFT JOIN token_1155_inventory ti ON th.token_address = ti.token_address AND th.token_id = ti.token_id WHERE th.balance != 0
AND th.address = #{address}
ORDER BY th.balance + 0
