mapper.select:
select th.token_address as tokenAddress, th.address, t.type, t.symbol, t.name, t.total_supply as totalSupply, th.balance, t.decimal, th.token_tx_qty as txCount from token_holder th left join token t on th.token_address = t.address where th.balance != 0
and th.token_address = #{tokenAddress}
and th.address = #{address}
AND t.type = #{type}
order by balance desc

SELECT th.token_address AS tokenAddress, th.address, ti.token_id AS tokenId, t.type, t.symbol, t.NAME, t.total_supply AS totalSupply, ti.token_owner_tx_qty AS txCount FROM token_inventory ti LEFT JOIN token_holder th ON th.token_address = ti.token_address LEFT JOIN token t ON th.token_address = t.address WHERE th.balance != 0
and th.token_address = #{tokenAddress}
AND th.address = #{address} AND ti.`owner` = #{address}
AND t.type = #{type}
ORDER BY balance DESC

SELECT th.token_address AS tokenAddress, th.address, t.type, t.symbol, t.NAME, t.total_supply AS totalSupply, th.balance, t.DECIMAL, ti.token_owner_tx_qty AS txCount, ti.token_id as tokenId FROM token_holder th LEFT JOIN token t ON th.token_address = t.address LEFT JOIN token_inventory ti ON th.address = ti.`owner` AND th.token_address = ti.token_address WHERE th.balance != 0
and th.token_address = #{tokenAddress}
and th.address = #{address}
AND t.type = "erc721" ORDER BY balance DESC

SELECT
th.token_address AS tokenAddress,
th.address
FROM token_holder th JOIN token t ON th.token_address = t.address
WHERE th.balance = 0 AND t.type = #{type}
ORDER BY #{orderby}
LIMIT #{offset}, #{limit}
