mapper.update:
/*1、更新质押*/
update `staking` set
    `is_consensus` = if(`node_id` in  #{validator} , 1,  2),
    `pre_cons_block_qty` = `cur_cons_block_qty`,
    `cur_cons_block_qty` = 0
where `status` in (1,  2);
/*2、更新节点*/
update `node` set
    `is_consensus` = if(`node_id` in  #{validator} , 1,  2),
    `stat_verifier_time` = if(`node_id` in  #{validator} , `stat_verifier_time` + 1,  `stat_verifier_time`),
    `stat_expect_block_qty` =  if(`node_id` in  #{validator} , `stat_expect_block_qty` + #{expectBlockNum},  `stat_expect_block_qty`)
where `status` = 1;
